import React from 'react'
import { GiCycle } from "react-icons/gi";

function Home() {
    const box={
        width:'70%',
        height:'80vh',
        backgroundColor:'#fff',
        position:'absolute',
        top:'100px',
        marginRight:'15%',
        marginLeft:'15%', 
        boxShadow:'0px 5px 15px black',
        
    }
    const text = {
        fontSize:'2.5rem',
        marginLeft:'20px'
    }
    const para={
        fontWeight:'bold',
        fontSize:'18px',
        marginLeft:'20px'
    }
    const border={
        width:'95%',
        height:'40vh',
        backgroundColor:'white',
        border:'2px dashed black',
        marginLeft:'20px',
    }
    const btn={
        backgroundColor:'black',
        color:'white',
        padding:'5px',
        margin:'20px 20px'
    }
    const icon={
        marginLeft:'20px'
    }
return (
        <div>
           <div style={box}>
            <h1 style={text}>UPLOAD IMAGE</h1>
            <p style={para}>Drag And Drop Your Images Below</p>
            <div style={border}>
            </div>
            <button style={btn} type='button'> <span style={icon} ><GiCycle/> </span>COMPRESS</button>
        </div></div>
    )
}
export default Home
